hibernate-mapping.meta:
	@Transient
	private String nome;
	
	@Transient
	private String nomeCompleto;
	
	@Transient
	private String tableName;
	
	@Transient
	private List<Atributo> listaAtributos;

	/**
	 * @return the nome
	 */
	public String getNome() {
		return nome;
	}

	/**
	 * @param nome the nome to set
	 */
	public void setNome(String nome) {
		this.nome = nome;
	}

	/**
	 * @return the nomeCompleto
	 */
	public String getNomeCompleto() {
		return nomeCompleto;
	}

	/**
	 * @param nomeCompleto the nomeCompleto to set
	 */
	public void setNomeCompleto(String nomeCompleto) {
		this.nomeCompleto = nomeCompleto;
	}

	/**
	 * @return the tableName
	 */
	public String getTableName() {
		return tableName;
	}

	/**
	 * @param tableName the tableName to set
	 */
	public void setTableName(String tableName) {
		this.tableName = tableName;
	}

	/**
	 * @return the listaAtributos
	 */
	public List<Atributo> getListaAtributos() {
		return listaAtributos;
	}

	/**
	 * @param listaAtributos the listaAtributos to set
	 */
	public void setListaAtributos(List<Atributo> listaAtributos) {
		this.listaAtributos = listaAtributos;
	}
