hibernate-mapping.meta:
	public Object getChave() {
		// return String.valueOf(getId());
		if (getId() != null) {
			return getId().toString();
		} else {
			return "";
		}
	}
	
	public String getDescricao() {
		StringBuilder b = new StringBuilder();
		String text = getCdNaturezaJuridica();
		boolean empty = text == null || text.trim().length() == 0;
		if (empty) {
			b.append(getCdNaturezaJuridica());
		}
		text = getDsNaturezaJuridica();
		empty = text == null || text.trim().length() == 0;
		if (empty) {
			b.append(" - " + getDsNaturezaJuridica());
		}

		return b.toString();
	}
