@Override
public boolean equals(Object obj) {
	boolean retorno = false;
	if (obj instanceof Pendencia) {
		Pendencia pendencia = (Pendencia) obj;
		if(this.id != null && pendencia.getId() != null){
			if (this.id.equals(pendencia.getId())) {
				retorno = true;
			}
		}
		else{
			if (this.valor.equalsIgnoreCase(pendencia.getValor())) {
				retorno = true;
			}
		}
	}
	return retorno;
}


@Override
public int hashCode() {
	final int prime = 31;
	int result = super.hashCode();
	result = prime * result + ((id == null) ? 0 : id.hashCode());
	return result;
}
