class.meta:
public void addPostoServico(PostoServico ps){
	if (postoServico == null) {
		postoServico = new HashSet();
	}
	postoServico.add(ps);
}

public void removePostoServico(PostoServico ps){
	if (postoServico != null) {
		postoServico.remove(ps);
	}
}

