class.meta:
public int compareTo(ModeloValorCaracteristica o) {
	return linha.compareTo(o.linha);
}

@Override
public String toString() {
	return valor;
}

public ModeloValorCaracteristica duplicate(DuplicateBag bag) {
	if(bag.fits(this)){
		ModeloValorCaracteristica mvc = new ModeloValorCaracteristica();
		bag.store(this,mvc);
		try {
			PropertyUtils.copyProperties(mvc,this);
		} catch (Exception e) {
			e.printStackTrace();
			throw new  RuntimeException(e);
		}
	}
	return (ModeloValorCaracteristica) bag.retrieve(this);
}

