class.meta:
@Override
public Object getChave() {
	return getCodigo();
}

@Override
public SituacaoModelo duplicate(DuplicateBag bag) {
	if(bag.fits(this)){
		SituacaoModelo sm = new SituacaoModelo();
		bag.store(this,sm);
		try {
			PropertyUtils.copyProperties(sm,this);
		} catch (Exception e) {
			e.printStackTrace();
			throw new  RuntimeException(e);
		}
	}
	return (SituacaoModelo) bag.retrieve(this);
}

