class.meta:
@Override
public Object getChave() {
	return getCodigo();
}

public TipoClassificacaoModelo duplicate(DuplicateBag bag) {
	if(bag.fits(this)){
		TipoClassificacaoModelo tcm = new TipoClassificacaoModelo();
		try {
			PropertyUtils.copyProperties(tcm,this);
		} catch (Exception e) {
			e.printStackTrace();
			throw new RuntimeException(e);
		}
		bag.store(this,tcm);
	}
	return (TipoClassificacaoModelo) bag.retrieve(this);
}

