class.meta:
@Override
public Object getChave() {
	return getCodigo();
}

public TipoDado duplicate(DuplicateBag bag){
	if(bag.fits(this)){
		TipoDado td = new TipoDado();
		bag.store(this,td);
		try {
			PropertyUtils.copyProperties(td,this);
		} catch (Exception e) {
			e.printStackTrace();
			throw new RuntimeException(e);
		}
	}
	return (TipoDado) bag.retrieve(this);
}

