class.meta:
@Override
public Object getChave() {
	return getCodigo();
}

@Override
public String getDescricao() {
	return nome;
}

public TipoModelo duplicate(DuplicateBag bag) {
	if(bag.fits(this)){
		TipoModelo tm = new TipoModelo();
		bag.store(this,tm);
		try {
			PropertyUtils.copyProperties(tm,this);
		} catch (Exception e) {
			e.printStackTrace();
			throw new RuntimeException(e);
		}
	}
	return (TipoModelo) bag.retrieve(this);
}

